(function() {
  "use strict";
  angular.module("app", [ "ngRoute", "app.directives" ]);
})();

(function() {
  "use strict";
  angular.module("app").controller("Master_Controller", Master_Controller).controller("Child_Controller", Child_Controller);
  function Master_Controller($log) {
    $log.log("Master_Controller loaded.");
  }
  Master_Controller.$inject = [ "$log" ];
  function Child_Controller($log) {
    $log.log("I logz it.");
  }
  Child_Controller.$inject = [ "$log" ];
})();

(function() {
  "use strict";
  angular.module("app.directives", []).directive("activeLink", activeLink);
  function activeLink($rootScope, $location) {
    return {
      restrict: "A",
      link: function postLink(scope, element, attrs, controller) {
        $rootScope.$on("$locationChangeSuccess", function() {
          if (!attrs.href || !$(element).is("a")) return;
          var isActive = false;
          var css = attrs.activeLinkClass || "active";
          var loc = ($location.path() || "").toLocaleLowerCase();
          if (attrs.href && attrs.href.toLocaleLowerCase() === loc) {
            isActive = true;
          } else if (attrs.activeLinkAltPaths && attrs.activeLinkAltPaths.length) {
            var paths = attrs.activeLinkAltPaths.split(",");
            isActive = _.some(paths, function(path) {
              return (path || "").toLocaleLowerCase() === loc;
            });
          }
          if (isActive) {
            $(element).addClass(css);
          } else {
            $(element).removeClass(css);
          }
        });
      }
    };
  }
  activeLink.$inject = [ "$rootScope", "$location" ];
})();

(function() {
  "use strict";
  angular.module("app").run(preloadTemplates);
  function preloadTemplates($log, $templateCache) {
    $templateCache.put("a", "<h1>Apple</h1>");
    $templateCache.put("b", "<h1>Banana</h1>");
    $templateCache.put("c", "<h1>Coconut</h1>");
  }
  preloadTemplates.$inject = [ "$log", "$templateCache" ];
})();